package com.hiveview.domybox.service.dao;import android.content.Context;import android.content.SharedPreferences;public class CommonPreferenceDAO {		private final String PREFERENCE = "LATESTLIKEPREFERENCE";	public final String CITIESID = "com.hiveview.tv.cities";		private SharedPreferences.Editor editor;	private SharedPreferences preference;		@SuppressWarnings("unused")	private CommonPreferenceDAO(){};		public CommonPreferenceDAO(Context context) {			preference = context.getSharedPreferences(PREFERENCE, Context.MODE_MULTI_PROCESS);		editor = preference.edit();			}			public void setCityId(String cityId){		editor.putString(CITIESID, cityId);		editor.commit();	}			public String getCityID(){		String cityId = preference.getString(CITIESID, "101010100");		return cityId;	}	}