package com.hiveview.domybox.service.parser;import java.io.InputStream;import java.util.ArrayList;import org.json.JSONException;import org.json.JSONObject;import com.hiveview.domybox.service.entity.BaseEntity;import com.hiveview.domybox.service.exception.ServiceException;import com.hiveview.domybox.utils.StringUtils;public class CommonParser extends BaseParser {		@Override	public ArrayList<? extends BaseEntity> executeToObject(InputStream in)throws ServiceException {				String jsonStr = StringUtils.converStreamToString(in);		try {			JSONObject obj = new JSONObject(jsonStr);			errorCode = obj.getString("code");					} catch (JSONException e) {			e.printStackTrace();			throw new ServiceException(10);		}		return null;	}		@Override	public String getErrorCode(){				return errorCode;			}		public String getDateString(){				return date;	}	}