package com.hiveview.domybox.service.request;import java.io.UnsupportedEncodingException;import java.util.ArrayList;import java.util.List;import org.apache.http.NameValuePair;import org.apache.http.client.entity.UrlEncodedFormEntity;import org.apache.http.client.methods.HttpPost;import org.apache.http.message.BasicNameValuePair;import com.hiveview.domybox.common.AppConstant;public class DevCheckRequest extends BasePostRequest {			private String deviceid;	private String mac;	private String uuid;		@SuppressWarnings("unused")	private DevCheckRequest() {}		public DevCheckRequest(String version, String deviceid, String mac, String uuid){		this.version 	= version;		this.deviceid 	= deviceid;		this.mac  		= mac;		this.uuid  		= uuid;			};			@Override	public HttpPost executeToEntity() throws UnsupportedEncodingException {				HttpPost httpPost = new HttpPost(String.format(HIVEVIEW_API_DEVICE_CHECK,  version));		List<NameValuePair> paramList = new ArrayList<NameValuePair>(AppConstant.NO_3);		paramList.add(new BasicNameValuePair(DEVICEID, deviceid));		paramList.add(new BasicNameValuePair(MAC, mac));		paramList.add(new BasicNameValuePair(UUID, uuid));		httpPost.setEntity(new UrlEncodedFormEntity(paramList,"UTF-8"));				return httpPost;	}}