package com.hiveview.domybox.view;import android.content.Context;import android.util.AttributeSet;import android.view.ViewDebug.ExportedProperty;import android.widget.TextView;/**跑马灯*/public class MarqueeText extends TextView{	private boolean isStart = false;	public MarqueeText(Context context) {		super(context);		init();	}		public MarqueeText(Context context, AttributeSet attrs) {		super(context, attrs);		init();	}	public MarqueeText(Context context, AttributeSet attrs, int defStyle) {		super(context, attrs, defStyle);		init();	}		public void init()	{	}		@Override	@ExportedProperty(category = "focus")	public boolean isFocused() {		return isStart;	}		public boolean isStart() {		return isStart;	}	public void setStart(boolean isStart) {		this.isStart = isStart;	}}