package com.hiveview.domybox.view;import android.content.Context;import android.graphics.drawable.AnimationDrawable;import android.util.AttributeSet;import android.widget.ImageView;import com.hiveview.domybox.R;public class ProgressDialog extends ImageView {	public ProgressDialog(Context context, AttributeSet attrs, int defStyle) {		super(context, attrs, defStyle);		init();	}	public ProgressDialog(Context context, AttributeSet attrs) {		super(context, attrs);		init();	}	public ProgressDialog(Context context) {		super(context);		init();	}	private void init() {		this.setImageResource(R.drawable.loading);		AnimationDrawable mAnimationDrawable = (AnimationDrawable) this.getDrawable();		mAnimationDrawable.start();			}	}